import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Navigation, User, Car, Menu, X, Phone, Mail, Star, Clock, Wallet } from 'lucide-react';
import { io } from 'socket.io-client';

// Main App Component
const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [userRole, setUserRole] = useState('rider'); 
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const [socket, setSocket] = useState(null);

  // Initialize with your personal details
  const [user, setUser] = useState({
    name: 'Parshant', 
    email: 'parshant@svsu.ac.in', 
    phone: '+91 98765 43210',
    profileImage: 'https://api.dicebear.com/7.x/avataaars/svg?seed=Parshant',
    rating: 5.0,
    totalRides: 0,
    balance: 0
  });

  // 1. Establish Real-time Connection when authenticated
  useEffect(() => {
    if (isAuthenticated) {
      const newSocket = io(); // Connects to backend via proxy
      setSocket(newSocket);

      newSocket.on('connect', () => {
        console.log('‚úÖ Real-time connection active:', newSocket.id);
      });

      return () => newSocket.close();
    }
  }, [isAuthenticated]);

  // 2. The Connection Bridge: Register User in MongoDB
  const handleLogin = async () => {
    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: user.name,
          email: user.email,
          password: 'password123' 
        })
      });
      
      const data = await response.json();
      if (data.success) {
        console.log("üöÄ Connection Successful: User saved in Database.");
        setIsAuthenticated(true);
      } else {
        console.warn("User might already exist, logging in...");
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error("‚ùå Backend unreachable. Is 'node server.js' running?", error);
      // Fallback for UI development
      setIsAuthenticated(true); 
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900">
      {!isAuthenticated ? (
        <LandingPage onLogin={handleLogin} />
      ) : (
        <>
          <Navbar 
            user={user}
            userRole={userRole}
            setUserRole={setUserRole}
            showMenu={showMenu}
            setShowMenu={setShowMenu}
            setCurrentView={setCurrentView}
          />
          
          <AnimatePresence mode="wait">
            {currentView === 'home' && (
              <MainDashboard 
                key="dashboard"
                userRole={userRole}
                user={user}
                setCurrentView={setCurrentView}
                socket={socket}
              />
            )}
            {currentView === 'profile' && (
              <ProfileView 
                key="profile"
                user={user}
                userRole={userRole}
              />
            )}
          </AnimatePresence>
        </>
      )}
    </div>
  );
};

// --- Landing Page ---
const LandingPage = ({ onLogin }) => {
  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="max-w-md w-full">
        <div className="text-center mb-8">
          <motion.div animate={{ rotate: [0, 10, -10, 0] }} transition={{ duration: 2, repeat: Infinity, repeatDelay: 3 }} className="inline-block mb-4">
            <Car className="w-16 h-16 text-yellow-400" />
          </motion.div>
          <h1 className="text-4xl font-bold text-white mb-2">Campus Ride-Share</h1>
          <p className="text-gray-300">Safe rides for students, by students</p>
        </div>

        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-8 shadow-2xl border border-white/20">
          <button onClick={onLogin} className="w-full bg-white text-gray-900 py-4 rounded-xl font-semibold mb-4 hover:bg-gray-100 transition-all transform hover:scale-105 shadow-lg">
            Sign in as {user.name}
          </button>
          <div className="mt-6 text-center text-sm text-gray-400">
            <p>Authenticated via University Portal</p>
            <p className="text-yellow-400 font-semibold">@svsu.ac.in</p>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

// --- Navbar Component ---
const Navbar = ({ user, userRole, setUserRole, showMenu, setShowMenu, setCurrentView }) => {
  return (
    <motion.nav initial={{ y: -100 }} animate={{ y: 0 }} className="bg-white/10 backdrop-blur-lg border-b border-white/20 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Car className="w-8 h-8 text-yellow-400" />
            <span className="text-white font-bold text-xl">CampusRide</span>
          </div>
          <div className="flex items-center space-x-4">
            <div className="bg-white/10 rounded-full p-1 flex">
              <button onClick={() => setUserRole('rider')} className={`px-6 py-2 rounded-full transition-all ${userRole === 'rider' ? 'bg-yellow-400 text-gray-900 font-semibold' : 'text-white'}`}>Rider</button>
              <button onClick={() => setUserRole('captain')} className={`px-6 py-2 rounded-full transition-all ${userRole === 'captain' ? 'bg-yellow-400 text-gray-900 font-semibold' : 'text-white'}`}>Captain</button>
            </div>
            <button onClick={() => setShowMenu(!showMenu)} className="text-white p-2 hover:bg-white/10 rounded-lg">
              {showMenu ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
          </div>
        </div>
      </div>
    </motion.nav>
  );
};

// --- Main Dashboard ---
const MainDashboard = ({ userRole, user, socket }) => {
  const [pickup, setPickup] = useState('');
  const [dropoff, setDropoff] = useState('');
  const [showBooking, setShowBooking] = useState(false);

  const handleFindRide = () => {
    setShowBooking(true);
    if(socket) {
      socket.emit('ride_request', { pickup, dropoff, rider: user.name });
    }
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="max-w-7xl mx-auto px-4 py-8">
      <div className="grid md:grid-cols-2 gap-8">
        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20 h-[500px] flex items-center justify-center">
            <div className="text-center">
              <MapPin className="w-16 h-16 text-yellow-400 mx-auto mb-4" />
              <p className="text-white text-lg font-semibold">Live Campus Map</p>
              <p className="text-gray-400 text-sm">Tracking active captains...</p>
            </div>
        </div>

        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20">
            <h2 className="text-white text-2xl font-bold mb-6">{userRole === 'rider' ? 'Book a Ride' : 'Captain Status'}</h2>
            {userRole === 'rider' ? (
              <div className="space-y-4">
                <input type="text" placeholder="Pickup (e.g., Library)" className="w-full bg-white/20 text-white rounded-xl py-3 px-4 border border-white/30" value={pickup} onChange={(e) => setPickup(e.target.value)} />
                <input type="text" placeholder="Drop-off (e.g., Hostel A)" className="w-full bg-white/20 text-white rounded-xl py-3 px-4 border border-white/30" value={dropoff} onChange={(e) => setDropoff(e.target.value)} />
                <button onClick={handleFindRide} className="w-full bg-yellow-400 text-gray-900 font-bold py-4 rounded-xl">Find Captain</button>
                {showBooking && <p className="text-green-400 text-center mt-2 animate-pulse">Searching for nearby rides...</p>}
              </div>
            ) : (
              <div className="text-center py-10">
                <p className="text-white mb-4">You are currently appearing as an available Captain.</p>
                <div className="w-4 h-4 bg-green-500 rounded-full mx-auto animate-ping"></div>
              </div>
            )}
          </div>
        </div>
      </div>
    </motion.div>
  );
};

// --- Profile View ---
const ProfileView = ({ user }) => (
  <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="max-w-4xl mx-auto px-4 py-8">
    <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-8 border border-white/20 text-white">
      <div className="flex items-center space-x-6">
        <img src={user.profileImage} alt="Profile" className="w-24 h-24 rounded-full border-4 border-yellow-400" />
        <div>
          <h1 className="text-3xl font-bold">{user.name}</h1>
          <p className="text-gray-400">{user.email}</p>
          <p className="text-yellow-400 mt-2">‚≠ê {user.rating} Rating</p>
        </div>
      </div>
    </div>
  </motion.div>
);

export default App;
