import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, User, Car, Menu, X, Phone, Mail, Star, Clock, Wallet } from 'lucide-react';
import { io } from 'socket.io-client';

const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [userRole, setUserRole] = useState('rider'); 
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const [socket, setSocket] = useState(null);

  // Your personal data state
  const [user, setUser] = useState({
    name: 'Parshant', 
    email: 'parshant@svsu.ac.in', 
    phone: '+91 98765 43210',
    profileImage: 'https://api.dicebear.com/7.x/avataaars/svg?seed=Parshant',
    rating: 5.0,
    totalRides: 0,
    balance: 0
  });

  useEffect(() => {
    if (isAuthenticated) {
      const newSocket = io(); 
      setSocket(newSocket);
      newSocket.on('connect', () => console.log('✅ Socket Active:', newSocket.id));
      return () => newSocket.close();
    }
  }, [isAuthenticated]);

  const handleLogin = async () => {
    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: user.name,
          email: user.email,
          password: 'password123' 
        })
      });
      const data = await response.json();
      if (data.success) setIsAuthenticated(true);
      else setIsAuthenticated(true); // Login anyway if user exists
    } catch (error) {
      console.error("Backend unreachable", error);
      setIsAuthenticated(true); 
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-gray-900">
      {!isAuthenticated ? (
        // FIXED: Passing 'user' as a prop here
        <LandingPage onLogin={handleLogin} user={user} />
      ) : (
        <>
          <Navbar 
            user={user}
            userRole={userRole}
            setUserRole={setUserRole}
            showMenu={showMenu}
            setShowMenu={setShowMenu}
            setCurrentView={setCurrentView}
          />
          <AnimatePresence mode="wait">
            {currentView === 'home' && (
              <MainDashboard 
                userRole={userRole}
                user={user}
                socket={socket}
              />
            )}
            {currentView === 'profile' && (
              <ProfileView user={user} userRole={userRole} />
            )}
          </AnimatePresence>
        </>
      )}
    </div>
  );
};

// --- Landing Page (Fixed Scope) ---
const LandingPage = ({ onLogin, user }) => {
  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} className="max-w-md w-full">
        <div className="text-center mb-8">
          <Car className="w-16 h-16 text-yellow-400 mx-auto mb-4" />
          <h1 className="text-4xl font-bold text-white mb-2">Campus Ride-Share</h1>
          <p className="text-gray-300 text-lg">Safe rides for students, by students</p>
        </div>

        <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-8 shadow-2xl border border-white/20">
          <button onClick={onLogin} className="w-full bg-white text-gray-900 py-4 rounded-xl font-bold mb-4 hover:bg-gray-100 transition-all shadow-lg">
            Sign in as {user.name}
          </button>
          <div className="text-center text-sm text-gray-400">
            <p>University verified account</p>
            <p className="text-yellow-400 font-mono">{user.email}</p>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

// --- Navbar ---
const Navbar = ({ userRole, setUserRole, showMenu, setShowMenu, setCurrentView }) => {
  return (
    <nav className="bg-white/10 backdrop-blur-lg border-b border-white/20 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex items-center justify-between">
        <div className="flex items-center space-x-2 text-white font-bold text-xl">
          <Car className="text-yellow-400" /> <span>CampusRide</span>
        </div>
        <div className="flex items-center space-x-4">
          <div className="bg-white/10 rounded-full p-1 flex text-sm">
            <button onClick={() => setUserRole('rider')} className={`px-4 py-1 rounded-full ${userRole === 'rider' ? 'bg-yellow-400 text-black' : 'text-white'}`}>Rider</button>
            <button onClick={() => setUserRole('captain')} className={`px-4 py-1 rounded-full ${userRole === 'captain' ? 'bg-yellow-400 text-black' : 'text-white'}`}>Captain</button>
          </div>
          <button onClick={() => setShowMenu(!showMenu)} className="text-white"><Menu /></button>
        </div>
      </div>
      {showMenu && (
        <div className="bg-gray-800 p-4 space-y-2">
          <button onClick={() => {setCurrentView('home'); setShowMenu(false)}} className="block text-white w-full text-left p-2">Home</button>
          <button onClick={() => {setCurrentView('profile'); setShowMenu(false)}} className="block text-white w-full text-left p-2">Profile</button>
        </div>
      )}
    </nav>
  );
};

// --- Main Dashboard ---
const MainDashboard = ({ userRole, user, socket }) => {
  const [showBooking, setShowBooking] = useState(false);

  const handleRide = () => {
    setShowBooking(true);
    if(socket) socket.emit('ride_request', { rider: user.name });
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8 grid md:grid-cols-2 gap-8">
      <div className="bg-white/5 rounded-2xl h-80 border border-white/10 flex items-center justify-center">
        <div className="text-center text-gray-400">
          <MapPin className="mx-auto mb-2 text-yellow-400" />
          <p>Map View Placeholder</p>
        </div>
      </div>
      <div className="space-y-4">
        <div className="bg-white/10 p-6 rounded-2xl border border-white/20">
          <h2 className="text-white text-xl font-bold mb-4">{userRole === 'rider' ? 'Request Ride' : 'Available Requests'}</h2>
          {userRole === 'rider' ? (
            <button onClick={handleRide} className="w-full bg-yellow-400 p-4 rounded-xl font-bold">Find Captain</button>
          ) : (
            <p className="text-gray-400 text-center py-4">Waiting for students...</p>
          )}
          {showBooking && <p className="text-green-400 text-center mt-4">Searching...</p>}
        </div>
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-purple-600/20 p-4 rounded-xl border border-purple-500/30">
            <Star className="text-yellow-400 mb-2" />
            <p className="text-white font-bold">{user.rating}</p>
            <p className="text-gray-400 text-xs">Rating</p>
          </div>
          <div className="bg-pink-600/20 p-4 rounded-xl border border-pink-500/30">
            <Wallet className="text-green-400 mb-2" />
            <p className="text-white font-bold">₹{user.balance}</p>
            <p className="text-gray-400 text-xs">Balance</p>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- Profile View ---
const ProfileView = ({ user }) => (
  <div className="max-w-4xl mx-auto px-4 py-8">
    <div className="bg-white/10 p-8 rounded-2xl border border-white/20 text-white flex items-center space-x-6">
      <img src={user.profileImage} alt="profile" className="w-20 h-20 rounded-full border-2 border-yellow-400" />
      <div>
        <h2 className="text-2xl font-bold">{user.name}</h2>
        <div className="flex space-x-4 text-sm text-gray-400 mt-2">
          <span className="flex items-center"><Mail className="w-4 h-4 mr-1"/> {user.email}</span>
          <span className="flex items-center"><Phone className="w-4 h-4 mr-1"/> {user.phone}</span>
        </div>
      </div>
    </div>
  </div>
);

export default App;
